;102&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;103&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;104&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;105&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;106&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;107&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;108&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;109&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;110&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;111&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;112&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;113&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;114&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;115&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;116&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;117&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;118&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;119&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;120&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;121&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;122&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;123&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;124&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;125&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;126&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;127&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;128&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;129&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;130&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;131&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;# encoding:utf-8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;from Crypto.Util.number import bytes_to_long, long_to_bytes&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;from gmpy2 import mpz, iroot, powmod, invert, is_prime&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;import sys&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;import multiprocessing as mp&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;# 定义常量&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;N = mpz(&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#x27;&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;# g是一个500位数，且g整除p-1和q-1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;g = mpz(&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#x27;3235645591686044532495326878291617484542045511433360748778013933565021819649890342389737893735574764418484922895778834144345295691799766302763199373647&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;e = 65537&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;C = mpz(&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#x27;&amp;#x27;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;# 计算辅助变量，使用整数除法 //&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;h = (N - 1) // g&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;u = h // g&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;v = h % g&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def worker_search(params):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;quot;&amp;quot;&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    工作进程函数，用于在指定的r和s范围内搜索c_exponent。&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;quot;&amp;quot;&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    r_start, r_end, s_start, s_end, D, b, final, N = params&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for r in range(r_start, r_end):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        target_exponent = r * D&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for s in range(s_start, s_end):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            c_exponent = target_exponent + s&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            current = powmod(b, c_exponent, N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            if current == final:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                return c_exponent&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return None&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def solve_c_parallel(num_processes=4, D_multiplier=2):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;quot;&amp;quot;&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    使用多进程并行化搜索c，使得 b^c ≡ final mod N。&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;quot;&amp;quot;&amp;quot;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    sqrt_N = iroot(N, 2)[0]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    C_approx = sqrt_N // (g * g)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    b = powmod(2, g, N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    final = powmod(b, u, N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for i in range(2, int(C_approx) + 1):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 计算D&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        D_root, is_exact = iroot(C_approx, 2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        D = (D_root + 1) * i&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 定义r和s的搜索范围，并将r的范围划分为多个块以并行处理&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        step = D // num_processes if D &amp;gt; num_processes else 1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        tasks = []&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for p in range(num_processes):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            r_start = p * step&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            r_end = (p + 1) * step if p &amp;lt; num_processes - 1 else D&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            s_start = 0&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            s_end = D&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            params = (r_start, r_end, s_start, s_end, D, b, final, N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            tasks.append(params)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 创建进程池并分配任务&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        with mp.Pool(processes=num_processes) as pool:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            results = pool.map(worker_search, tasks)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 检查是否有任何工作进程找到了c_exponent&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for res in results:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            if res is not None:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                print(f&amp;quot;Solution found: c = &amp;#123;res&amp;#125;&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                return res&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    return None&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;def main():&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    c = solve_c_parallel()&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    if c is not None:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        print(&amp;quot;c:&amp;quot;, c)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 计算A和B&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        A = u - c&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        B = v + c * g&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 求解二次方程x^2 - Bx + A = 0&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        discriminant = B * B - 4 * A&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        sqrt_discriminant, is_perfect_square = iroot(discriminant, 2)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if not is_perfect_square or sqrt_discriminant * sqrt_discriminant != discriminant:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            print(&amp;quot;判别式不是完全平方数。&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            sys.exit(1)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        x = (B + sqrt_discriminant) // 2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        y = (B - sqrt_discriminant) // 2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 计算a和b&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        a_val = x // 2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        b_val = y // 2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 计算p和q&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        p = 2 * g * a_val + 1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        q = 2 * g * b_val + 1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 验证p和q是否为素数&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if not is_prime(p):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            print(&amp;quot;p 不是素数。&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            sys.exit(1)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        if not is_prime(q):&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            print(&amp;quot;q 不是素数。&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            sys.exit(1)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 计算phi(N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        phi_N = (p - 1) * (q - 1)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 计算私钥指数d&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        try:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            d = invert(e, phi_N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        except ZeroDivisionError:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            print(&amp;quot;e与phi(N)不互质，无法找到逆元。&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            sys.exit(1)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        # 解密密文&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        m = powmod(C, d, N)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        decrypted_message = long_to_bytes(m)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        print(&amp;quot;解密消息:&amp;quot;, decrypted_message)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    else:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        print(&amp;quot;未找到c的解。&amp;quot;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;if __name__ == &amp;#x27;__main__&amp;#x27;:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    main()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;p&gt;Solution found: c = 51589121&lt;br&gt;
