          &lt;span class=&#34;keyword&#34;&gt;while&lt;/span&gt; (tcache-&amp;gt;counts[tc_idx] &amp;lt; mp_.tcache_count&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                     &amp;amp;&amp;amp; (tc_victim = last (bin)) != bin)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                  &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (tc_victim != &lt;span class=&#34;number&#34;&gt;0&lt;/span&gt;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                      bck = tc_victim-&amp;gt;bk;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                      set_inuse_bit_at_offset (tc_victim, nb);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                      &lt;span class=&#34;keyword&#34;&gt;if&lt;/span&gt; (av != &amp;amp;main_arena)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                        set_non_main_arena (tc_victim);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                      bin-&amp;gt;bk = bck;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                      bck-&amp;gt;fd = bin;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                      tcach                   &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;meta&#34;&gt;#&lt;span class=&#34;keyword&#34;&gt;endif&lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;          &lt;span class=&#34;type&#34;&gt;void&lt;/span&gt; *p = chunk2mem (victim);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;          alloc_pertur